import React from 'react'
import styles from './Testpage.module.scss'
import img1 from '../../img/bmw1.jpg'

const Testpage = () => {
    const slider = React.useRef(null)
    const [ prev,setPrev ] = React.useState(false)
    const [ next,setNext ] = React.useState(false)
    
    let position = 0

    const prevHandler = () => {
        if (position === 0) {
            setPrev(true)
        } else {
            position += 150
            slider.current.childNodes.forEach(element => {
                element.style = `transform: translateX(${position}px)`
            })
        }

    }

    const nextHandler = () => {
        if (position <= -(6 - 3)* 150) {
            setNext(true)
        } else {
            position -= 150
            console.log(slider.current.childNodes)
            slider.current.childNodes.forEach(element => {
                element.style = `transform: translateX(${position}px)`
            })
        }
    }
    return (
        <div className={styles.container}>
            <div className={styles.productPage__imageGallery__block}>
                <ul className={styles.imageGallery__topSlider}>
                            <span className={styles.imageGallery__topSlider__leftArrow}>
                                <svg width="8" height="12" viewBox="0 0 8 12" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M7 1L2 6L7 11" stroke="#3E3EAC" strokeWidth="2"/>
                                </svg>
                            </span>
                            <span className={styles.imageGallery__topSlider__rightArrow}>
                                <svg width="8" height="12" viewBox="0 0 8 12" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M1 1L6 6L1 11" stroke="#3E3EAC" strokeWidth="2"/>
                                 </svg>
                            </span>
                            <li className={styles.imageGallery__topSlider__item}>
                                <img src={img1} alt="bmw1" className={styles.imageGallery__topSlider__image} />
                            </li>
                </ul>


                <div className={styles.imageGallery__bottomSlider}>
                    <span className={styles.bottomSlider__leftArrow} onClick={prevHandler}>
                                        <svg width="8" height="12" viewBox="0 0 8 12" fill="none" xmlns="http://www.w3.org/2000/svg">
                                            <path d="M7 1L2 6L7 11" stroke="#3E3EAC" stroke-width="2"/>
                                        </svg>
                        </span>
                        <span className={styles.bottomSlider__rightArrow} onClick={nextHandler}>
                                        <svg width="8" height="12" viewBox="0 0 8 12" fill="none" xmlns="http://www.w3.org/2000/svg">
                                            <path d="M1 1L6 6L1 11" stroke="#3E3EAC" stroke-width="2"/>
                                        </svg>
                        </span>
                     <ul className={styles.bottomSlider__track} ref={slider}>
                        <li className={styles.bottomSlider__item}>
                            <img src={img1} className={styles.bottomSlider__image}  alt="img"/>
                        </li>
                        <li className={styles.bottomSlider__item}>
                            <img src="data:image/jpeg;base64," className={styles.bottomSlider__image}  alt="img"/>
                        </li>
                        <li className={styles.bottomSlider__item}>
                            <img src={img1} className={styles.bottomSlider__image}  alt="img" />
                        </li>
                        <li className={styles.bottomSlider__item}>
                            <img src="data:image/jpeg;base64," className={styles.bottomSlider__image}  alt="img"/>
                        </li>
                        <li className={styles.bottomSlider__item}>
                            <img src={img1} className={styles.bottomSlider__image} />
                        </li>
                    </ul>
                </div>

            </div>
        </div>
    )
}

export default Testpage
